	/**
	 * 获取上一个月
	 *
	 * @param year  年
	 * @param month 月
	 * @return String（格式化的时间）
	 */
	public static String getLastDateOfMonth(int year, int month, String pattern) {
		if (StringUtils.isBlank(pattern)) {
			throw new NullPointerException(PATTERN_TYPE_IS_NULL);
		}
		// 得到一个Calendar的实例
		Calendar calendar = Calendar.getInstance();
		// 月份是从0开始的，所以月份需要减1
		calendar.set(year, month - 1, 17);
		// 月份减1，就是上一月份
		calendar.add(Calendar.MONTH, -1);
		Date date = calendar.getTime();
		SimpleDateFormat simpleDateFormat = new SimpleDateFormat(pattern);
		return simpleDateFormat.format(date);
	}

	/**
	 * 获取近期六个月数据(包含本月)
	 *
	 * @param year  年
	 * @param month 月份
	 * @return @return String[]
	 */
	public static String[] getNearSixMonth(int year, int month) {
		if (month >= 1 && month <= 12) {
			String[] sixMonthArray = new String[6];
			//将本月放入第一个
			if (month >= 10) {
				sixMonthArray[0] = year + "" + month;
			} else {
				sixMonthArray[0] = year + "0" + month;
			}
			for (int i = 1; i < 6; i++) {
				String lastDateOfMonth = getLastDateOfMonth(year, month, DateConstant.DATE_YEAR_MONTH);
				sixMonthArray[i] = lastDateOfMonth;
				//如果月份为1月份，则需要从前一年的12月份算，所以月份就为12，年份需要减1
				if (month == 1) {
					month = 12;
					year = year - 1;
				} else {
					month--;
				}
			}
			return sixMonthArray;
		} else {
			return new String[0];
		}
	}

