	/**
	 * 获取两个日期之间的所有日期列表(包含日期和结束日期)
	 *
	 * @param startDate 开始日期
	 * @param endDate   结束日期
	 * @param pattern   模式
	 * @return List<String>
	 */
	public static List<String> getDateListBetween(String startDate, String endDate, String pattern) {
		if (StringUtils.isBlank(startDate) || StringUtils.isBlank(endDate) || StringUtils.isBlank(pattern)) {
			throw new NullPointerException(PARAMETER_IS_NULL);
		}
		List<String> dateList = new ArrayList<>();

		LocalDate startLocalDate = LocalDate.parse(startDate, DateTimeFormatter.ofPattern(pattern));
		LocalDate endLocalDate = LocalDate.parse(endDate, DateTimeFormatter.ofPattern(pattern));
		while (endLocalDate.isAfter(startLocalDate)) {
			String date = startLocalDate.format(DateTimeFormatter.ofPattern(pattern));
			dateList.add(date);
			startLocalDate = startLocalDate.plusDays(1);
		}
		// 加入最后一天
		dateList.add(endDate);
		return dateList;
	}

	/**
	 * 一个日期是否在另一个日期之后或两个日期是否相等
	 *
	 * @param startDate 开始日期
	 * @param endDate   结束日期
	 * @param pattern   模式
	 * @return boolean
	 */
	public static boolean isAfterOrEqual(String startDate, String endDate, String pattern) {
		if (StringUtils.isBlank(startDate) || StringUtils.isBlank(endDate) || StringUtils.isBlank(pattern)) {
			throw new NullPointerException(PARAMETER_IS_NULL);
		}
		LocalDate startLocalDate = LocalDate.parse(startDate, DateTimeFormatter.ofPattern(pattern));
		LocalDate endLocalDate = LocalDate.parse(endDate, DateTimeFormatter.ofPattern(pattern));
		return endLocalDate.isAfter(startLocalDate) || endLocalDate.isEqual(startLocalDate);
	}


